    this.setInitiallyExpanded();
    this.searchInputText.setValue(this.searchText);
    this.searchInputText.valueChanges
      .pipe(debounceTime(this.debounceInterval))
      .subscribe((text) => {
        this.input.emit(text || '');
        this.modelValueChange.emit(text || '');
      });
  }

